using Test
using XPORTA

@testset "closure" begin

include("../../BellScenario.jl/src/BellScenario.jl")
using BellScenario: Symmetry, LocalPolytope, BellGame, LocalSignaling
ieq = XPORTA.read_ieq("./files/51-2-15_canonical_facets.ieq")

porta_facets = ieq.inequalities

PM = LocalSignaling(5,5,2)

canonical_games = []
# iter = 0
for porta_facet in eachrow(porta_facets)
    # iter = iter + 1
    # println("iteration : ",it)
    gen_facet = convert.(Int,cat([-1*porta_facet[end]], porta_facet[1:(end-1)], zeros(Int64, 5), dims=1))
    (bound, facet_matrix) = LocalPolytope.facet_to_matrix(5, 5, gen_facet)
    canonical_facet = Symmetry.generator_facet(BellGame(convert.(Int64,facet_matrix), bound), PM)
    push!(canonical_games, canonical_facet)
end


